export default function AddToInput(t,...e){if("input"!==t.tagName.toLowerCase())throw new Error("El parametro input no es un input");e.forEach((e,a)=>{if(null===e.getAttribute("data"))throw new Error(`El parametro button ${a} no tiene el atributo data`);e.addEventListener("click",e=>{""===t.value||null===t.value||void 0===t.value?t.value=e.target.getAttribute("data"):t.value=t.value.trimEnd().concat(", ",e.target.getAttribute("data")),t.dispatchEvent(new Event("input",{bubbles:!0}))})})}